public class Temperaturas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double F=70, C; //
		C=(F-32)/1.8; //Hacemos la ecuación para trasformar de Fahrenheit a Celsius.
		System.out.println("Siendo la temperatura en grados Fahrenheit: "+F+"°F"+" la temperatura en Celsius es: "+C+"°C");
	}

}
